import { Request, Response, Router } from 'express'
import EmailValidator from 'email-validator'

import { validatePostInputs, PostModel } from './../models/PostModel'

import PostController from '../controllers/PostController'
import UserController from '../controllers/UserController'

export const postRouter = Router()
const postCtrl = new PostController()
const userCtrl = new UserController()

postRouter.post('/', async (req: Request, res: Response) => {
  const errorMessages = validatePostInputs(req.body)

  if (errorMessages.length == 0) {
    const { title, content, user } = req.body
    const userFound = await userCtrl.findByEmail(user)
    if (userFound) {
      const post = new PostModel({
        title,
        content,
        user: userFound._id,
      })

      const savedPost = await postCtrl.save(post)
      return res.status(201).json({ savedPost })
    }

    return res.status(403).json({ message: 'User not found' })
  }
  return res.status(400).json({ errorMessages })
})

postRouter.get('/:userEmail', async (req: Request, res: Response) => {
  const { userEmail } = req.params

  if (EmailValidator.validate(userEmail)) {
    const userFound = await userCtrl.findByEmail(userEmail)
    if (userFound) {
      const posts = await postCtrl.findByUserId(userFound._id)
      return res.json({ posts })
    }

    return res.status(404).json({ message: 'User not found' })
  }

  return res.status(400).json({ message: 'Invalid e-mail' })
})

import { Document, Schema, model } from 'mongoose'

import { User } from './UserModel'

export interface Post extends Document {
  title: string
  content: string
  user: User | number
}

const schema = new Schema({
  title: { type: String, required: true },
  content: { type: String, required: true },
  user: { type: Schema.Types.ObjectId, ref: 'UserModel' },
})

export const validatePostInputs = (postObj: any) => {
  const { title, content, user } = postObj
  const errorMessages: string[] = []

  if (!title) {
    errorMessages.push('Invalid title')
  }

  if (!content) {
    errorMessages.push('Invalid content')
  }

  if (!user) {
    errorMessages.push('No user associated to the post')
  }

  return errorMessages
}

export const PostModel = model('Post', schema)

import { PostModel } from './../models/PostModel'

import { Post } from '../models/PostModel'

export default class PostController {
  async save(post: Post): Promise<Post> {
    const savedPost = await PostModel.create(post)
    return savedPost
  }

  async findByUserId(userId: number): Promise<Post[]> {
    const posts: Post[] = await PostModel.find({ user: userId })
    return posts
  }
}

import { User, UserModel } from '../models/UserModel'

export default class UserController {
  async save(user: User): Promise<User> {
    const savedUser = await UserModel.create(user)
    return savedUser
  }

  async findAll(): Promise<User[]> {
    const users = await UserModel.find()
    return users
  }

  async findByEmail(email: string): Promise<User> {
    const user = await UserModel.findOne({ email })
    return user
  }
}

import { Document, Schema, model } from 'mongoose'
import EmailValidator from 'email-validator'

export interface User extends Document {
  name: string
  email: string
  birthday: Date
}

const schema = new Schema<User>({
  name: { type: String, required: true },
  email: { type: String, required: true },
  birthday: { type: Date, required: true },
})

export const validateUserInputs = (userObj: any) => {
  const { name, email, birthday } = userObj
  const errorMessages: string[] = []
  if (!name) {
    errorMessages.push('Invalid name')
  }

  if (!EmailValidator.validate(email)) {
    errorMessages.push('Invalid e-mail')
  }

  if (birthday.match(/^\d{4}-\d{2}-\d{2}$/)) {
    const birthdayObj = new Date(birthday)
    if (birthdayObj >= new Date()) {
      errorMessages.push('Invalid birthday')
    }
  } else {
    errorMessages.push('Invalid birthday')
  }

  return errorMessages
}

export const UserModel = model<User>('User', schema)

import { Request, Response, Router } from 'express'

import { validateUserInputs } from './../models/UserModel'

import UserController from '../controllers/UserController'
import { UserModel } from '../models/UserModel'

export const userRouter = Router()
const userCtrl = new UserController()

userRouter.post('/', async (req: Request, res: Response) => {
  const errorMessages = validateUserInputs(req.body)

  if (errorMessages.length == 0) {
    const { name, email, birthday } = req.body

    const user = new UserModel({
      name,
      email,
      birthday: new Date(birthday),
    })

    const savedUser = await userCtrl.save(user)
    return res.status(201).json({ savedUser })
  }

  // STATUS 400 -> BAD REQUEST
  return res.status(400).json({ errorMessages })
})

userRouter.get('/', async (req: Request, res: Response) => {
  const users = await userCtrl.findAll()
  return res.json({ users })
})

